import React, { useState } from 'react';

const Modal = props => {
  const [isOpen, setIsOpen] = useState(true);

  const toggle = () => {
    setIsOpen(!isOpen);
  };

  if (!isOpen) {
    return null;
  }
  return (
    <div className="modal-background" onClick={() => toggle()}>
      <div className="modal-container">{props.children}</div>
    </div>
  );
};

export default Modal;
