import React, { Component } from 'react';
import { w3cwebsocket as W3CWebSocket } from 'websocket';

const client = new W3CWebSocket('ws://127.0.0.1:8000');

export default class Test extends Component {
  componentDidMount() {
    var watchID = navigator.geolocation.watchPosition(function(position) {
      this.sendMessage(position.coords.latitude, position.coords.longitude);
    });
    client.onopen = () => {
      console.log('WebSocket Client Connected');
    };
    client.onmessage = message => {
      console.log(message);
    };
  }

  sendMessage = (lat, long) => {
    const data = JSON.stringify({ lat, long });
    client.send(data);
  };

  render() {
    return (
      <div class="row">
        <div class="input-field col s6">
          <input value="Alvin" id="first_name2" type="text" class="validate" />
          <label class="active" for="first_name2">
            First Name
          </label>
        </div>
        <button onClick={this.sendMessage} class="btn waves-effect waves-light">
          Click Me
        </button>
      </div>
    );
  }
}
