import React from 'react';
import '../Modal/Modal.css';

export default class Modal extends React.Component {
  render() {
    if (!this.props.show) {
      return null;
    }
    return (
      <div className="modal-background ">
        <div className="modal-container" onClick={e => this.props.toggle(e)}>
          {this.props.children}
        </div>
      </div>
    );
  }
}
