import React, { useState } from 'react';

const Modal = () => {
  const [isOpen, setIsOpen] = useState(false);

  if (!this.props.show) {
    return null;
  }
  return (
    <div className="modal-background">
      <div className="modal-container">{this.props.children}</div>
    </div>
  );
};

export default Modal;
